import React, { FC, memo, ReactNode, useMemo } from 'react';
import { Image, ImageSourcePropType, StyleProp, ViewStyle } from 'react-native';
import { SvgXml } from 'react-native-svg';

import Box from '../box';
import Button from '../button';
import { px } from '../helpers/normalize';
import Text from '../text';

export interface ActionButtonProps {
  title: string;
  type: 'primary' | 'secondary';
  onPress: () => void;
}

export interface ResultProps {
  /** 主文字 */
  title?: ReactNode;
  /** 内容文字 */
  content?: ReactNode;
  /** 自定义图片 */
  imgSource?: ImageSourcePropType;
  /** 类型（成功、失败、进行中） */
  type: 'success' | 'fail' | 'process';
  /** 操作项 */
  actions?: ActionButtonProps[];
  /** 容器样式 */
  containerStyle?: StyleProp<ViewStyle>;
}

const Result: FC<ResultProps> = ({ actions = [], type, title, content, imgSource, containerStyle }) => {
  const ImgByType = useMemo(() => {
    if (imgSource) return <Image source={imgSource} style={{ width: px(190), height: px(190) }} resizeMode="contain" />;

    switch (type) {
      case 'success':
        return <SuccessImg />;
      case 'fail':
        return <FailImg />;
      case 'process':
        return <ProcessImg />;
      default:
        return null;
    }
  }, [imgSource, type]);

  const Title = useMemo(() => {
    if (!title) return null;
    if (typeof title === 'string') {
      return (
        <Text variant="h1" color="text" marginBottom="x2">
          {title}
        </Text>
      );
    }
    return title;
  }, [title]);

  const Content = useMemo(() => {
    if (!content) return null;
    if (typeof content === 'string') {
      return (
        <Text variant="p1" color="text" marginBottom="x2">
          {content}
        </Text>
      );
    }
    return content;
  }, [content]);

  return (
    <Box padding="x6" marginTop="x6" justifyContent="center" alignItems="center" style={containerStyle}>
      {ImgByType}
      {Title}
      {Content}
      {actions.length > 0 && (
        <Box width="100%">
          {actions.map((action, index) => (
            <Box key={index} marginBottom="x2">
              <Button title={action.title} type={action.type} onPress={action.onPress} />
            </Box>
          ))}
        </Box>
      )}
    </Box>
  );
};
Result.displayName = 'Result';

export default Result;

